import { Text,TouchableOpacity,View, Image } from "react-native";
import { COLORS, SIZES } from "../../constants";
import styles from "./productCardView.style";
import {Ionicons} from "@expo/vector-icons"
import { useNavigation } from "@react-navigation/native";

const ProductCardView = () => {
    const navigation = useNavigation();
    const product = [1,2,3,4]
    return(
        <TouchableOpacity onPress={()=>navigation.navigate("ProductDetails")}>
            <View style={styles.container}>
                <View style={styles.imageContainer}>
                    <Image
                        source={{uri: "https://d326fntlu7tb1e.cloudfront.net/uploads/5d445b91-c01a-4564-8ff8-c27c2b88ea5b-fn7.png" }}
                        style={styles.image}
                    />
                </View>
                    <View style={styles.details}>
                        <Text style={styles.title} numberOfLines={1}>Product</Text>
                        <Text style={styles.supplier} numberOfLines={1}>Product</Text>
                        <Text style={styles.price}>$2314</Text>
                    </View>
                    <TouchableOpacity style={styles.addBtn}>
                        <Ionicons name="add-circle" size={35} color={COLORS.primary}/>
                    </TouchableOpacity>
            </View>
        </TouchableOpacity>
    )
}

export default ProductCardView
